
create database NBFC_Customers;

use NBFC_Customers;

CREATE TABLE IF NOT EXISTS NBFC_Customers (
    id INT PRIMARY KEY,
    full_name VARCHAR(100),
    city VARCHAR(50),
    loan_type VARCHAR(20),
    loan_amount INT,
    interest_rate DECIMAL(5,2),
    loan_term INT,
    start_date DATE,
    status VARCHAR(20)
);


CREATE TABLE IF NOT EXISTS Query_History (
    id INT AUTO_INCREMENT PRIMARY KEY,
    query_text TEXT,
    executed_at DATETIME DEFAULT CURRENT_TIMESTAMP
);



ALTER TABLE NBFC_Customers
ADD COLUMN loan_age_years INT DEFAULT 0;

UPDATE NBFC_Customers
SET loan_age_years = TIMESTAMPDIFF(YEAR, start_date, CURDATE());










SELECT loan_amount * (loan_term / (interest_rate/100 + 1)^(loan_term*365/loan_amortization_days)) as repayment_capacity
FROM NBFC_Customers
WHERE city = 'Pune' AND status = 'active' AND DATEDIFF(CURDATE(), start_date) >= 1825;
